import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import RoomIcon from '@material-ui/icons/Room';

import Icon from '@material-ui/core/Icon';

class App extends Component {


  add=() =>{
    let room = 1;
    let adult = 1;
    let children =1;
    if(adult >=1){
      adult++;
    }

  }


  render() {
    const styles = theme => ({
      root: {
        width: '100%',
        marginTop: theme.spacing.unit * 3,
        overflowX: 'auto',
      },
      table: {
        minWidth: 700,
      },
    });

    let id = 0;
    function createData(name, calories, fat, carbs, protein) {
      id += 1;
      return { id, name, calories, fat, carbs, protein };
    }

    const rows = [
      createData('Rooms'),
      createData('Adults'),
      createData('Children'),

    ]
    return (
      <div className="App" style={{paddingTop: "136px", width:500, paddingLeft:600 }}>
      <Paper className="">
      <Table className="" >
        <TableHead>
          <TableRow>
            <TableCell><strong>Choose A Number Of People</strong></TableCell>
            {/* <TableCell numeric><RemoveIcon /> </TableCell>
            <TableCell numeric>Fat (g)</TableCell>
            <TableCell numeric>Carbs (g)</TableCell>
            <TableCell numeric>Protein (g)</TableCell>  */}
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map(row => {
            return (
              <TableRow key={row.id}>
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell numeric><Button variant="fab" color="primary" aria-label="Remove" >
                     <RemoveIcon />
              </Button></TableCell>
                <TableCell numeric> <Button variant="fab" color="secondary" aria-label="Add" onClick={this.add}>
                     <AddIcon />
              </Button></TableCell>
                {/* <TableCell numeric>{row.Children}</TableCell>
                <TableCell numeric>{row.protein}</TableCell> */}
              </TableRow>
            );
          })}
        </TableBody>
      </Table>
    </Paper>
      </div>
    );
  }
}

export default App;
