import React, { Component } from 'react';
import './App.css';

import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';

import Icon from '@material-ui/core/Icon';

class App extends Component {
  state={
    updateRoom:1,
    decrementRoom:0,
    addult:1,
    child:0,
    show: true
  }



  subtractRoom=() =>{
    this.setState({ updateRoom: this.state.updateRoom - 1 });
    console.log("i am sub", this.state.updateRoom)
  }

    addRoom=() =>{

      this.setState({ updateRoom: this.state.updateRoom+1 })

      console.log(this.state.updateRoom)

    }

    addAdults=()=>{
      if(this.state.child || !this.state.adult ){
        this.setState({ updateRoom:this.state.updateRoom+1 });
         this.setState({ child:this.state.child+1 });
      }else if(!this.state.child || this.state.adult ){
          this.setState({ addult:this.state.addult+1 });
        this.setState({ updateRoom:this.state.updateRoom+1 });

      }
      else if(!this.state.child || !this.state.adult ){
         this.setState({ child:this.state.child-1 })


      }
      console.log("addAdults")
    }

    // subtractAdults=()=>{
    //   console.log("subtractAdults")
    // }

    // addChildrens=()=>{
    //   if(!this.state.child || !this.state.adult ){
    //     this.setState({updateRoom:this.state.updateRoom+1});
    //   }else if(!this.state.child || !this.state.adult ){
    //     this.setState({updateRoom:this.state.updateRoom-1});
    //   }
    //   console.log("add")
    // }

    subtractChildrens=()=>{
      console.log("subtract")
      if(!this.state.child || !this.state.adult ){
        this.setState({ updateRoom:this.state.updateRoom-1 });
         this.setState({ child:this.state.child-1 });
      }


    }

    ToggleClick = () => {
      this.setState({ show: !this.state.show });
  }
  render() {

    return (

    <div className="App" style={{paddingTop: "136px", width:500, paddingLeft:600 }}>
     <Paper >
    <Table style={{paddingTop: "136px", width:500, paddingLeft:600 }}>
      <TableRow>
        <th>Choose A Number Of People</th>
        <th>Current</th>
        <th>Room + Clicked</th>
      </TableRow>
      <tr>
        <td><i className="material-icons">room</i>Room</td>
        <td><Button variant="fab" color="primary" aria-label="Remove" >
        <RemoveIcon />

        </Button></td>
        <td>{this.state.updateRoom}<Button variant="fab" color="secondary" aria-label="Add" ><AddIcon /></Button></td>
      </tr>
      <tr>
        <td><i className="material-icons">account_circle</i>Adults</td>
        <td><Button variant="fab" color="primary" aria-label="Remove" onClick={this.subtractChildrens}><RemoveIcon /></Button></td>
        <td>{this.state.addult}  <Button variant="fab" color="secondary" aria-label="Add" onClick={this.addAdults}>  <AddIcon /></Button></td>
      </tr>
      <tr>
        <td><i className="material-icons">accessibility_new</i>Children</td>
        <td><Button variant="fab" color="primary" aria-label="Remove" onClick={this.subtractChildrens}><RemoveIcon /></Button></td>
        <td>{this.state.child}<Button variant="fab" color="secondary" aria-label="Add" onClick={this.addAdults}><AddIcon /></Button></td>
      </tr>

</Table>
 </Paper>
    </div>
    );
  }
}

export default App;
