import React, { Component } from 'react';
import './App.css';

import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';

import Icon from '@material-ui/core/Icon';

class App extends Component {
  state={
    updateNumber:''
  }


  add=() =>{
    let room = 1;
    let adult = 1;
    let children =1;
    let total;
    if(adult >=1){
     adult = adult++;
     this.setState({updateNumber:adult})
      console.log(adult)
    }

  }

  subtractRoom=() =>{
    console.log("i am sub")
  }

    addRoom=() =>{
      console.log('adults')
    }

  render() {

    return (

    <div className="App" style={{paddingTop: "136px", width:500, paddingLeft:600 }}>
     <Paper >
    <Table style={{paddingTop: "136px", width:500, paddingLeft:600 }}>
      <TableRow>
        <th>Choose A Number Of People</th>
        <th>Current</th>
        <th>Room + Clicked</th>
      </TableRow>
      <tr>
        <td><i className="material-icons">room</i>Room</td>
        <td><Button variant="fab" color="primary" aria-label="Remove" onClick={this.subtractRoom}><RemoveIcon /></Button></td>
        <td><Button variant="fab" color="secondary" aria-label="Add" onClick={this.addRoom}><AddIcon /></Button></td>
      </tr>
      <tr>
        <td><i className="material-icons">account_circle</i>Adults</td>
        <td><Button variant="fab" color="primary" aria-label="Remove" onClick={this.subtract}><RemoveIcon /></Button></td>
        <td><Button variant="fab" color="secondary" aria-label="Add" onClick={this.add}><AddIcon /></Button></td>
      </tr>
      <tr>
        <td><i className="material-icons">accessibility_new</i>Children</td>
        <td><Button variant="fab" color="primary" aria-label="Remove" onClick={this.subtract}><RemoveIcon /></Button></td>
        <td><Button variant="fab" color="secondary" aria-label="Add" onClick={this.add}><AddIcon /></Button></td>
      </tr>

</Table>
 </Paper>
    </div>
    );
  }
}

export default App;
