import React, { Component } from 'react';
import './App.css';

import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';

import Icon from '@material-ui/core/Icon';

class App extends Component {
  state={
    updateNumber:''
  }


  add=() =>{
    let room = 1;
    let adult = 1;
    let children =1;
    let total;
    if(adult >=1){
     adult = adult++;
     this.setState({updateNumber:adult})
      console.log(adult)
    }

  }

  subtract=() =>{
    console.log("i am sub")
  }


  render() {
    const styles = theme => ({
      root: {
        width: '100%',
        marginTop: theme.spacing.unit * 3,
        overflowX: 'auto',
      },
      table: {
        minWidth: 700,
      },
    });

    let id = 0;
    function createData(name, current, room) {
      id += 1;
      return { id, name, current, room };
    }

    const rows = [
      createData('Rooms'),
      createData('Adults'),
      createData('Children'),

    ]
    return (
      <div className="App" style={{paddingTop: "136px", width:500, paddingLeft:600 }}>
      <Paper className="">
      <Table className="" >
        <TableHead>
          <TableRow>
            <TableCell><strong>Choose A Number Of People</strong></TableCell>
            <TableCell >Current</TableCell>
            <TableCell numeric >Room  + Clicked</TableCell>

          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map(row => {
            return (
              <TableRow key={row.id}>
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell numeric><Button variant="fab" color="primary" aria-label="Remove" onClick={this.subtract}>
                     <RemoveIcon />
              </Button></TableCell>
                <TableCell numeric>{this.state.updateNumber}<Button variant="fab" color="secondary" aria-label="Add" onClick={this.add}>

                     <AddIcon />

              </Button></TableCell>
                {/* <TableCell numeric>{row.Children}</TableCell>
                <TableCell numeric>{row.protein}</TableCell> */}
              </TableRow>
            );
          })}
        </TableBody>
      </Table>
    </Paper>
    <div>
    <Table>
  <tr>
    <th>Choose A Number Of People</th>
    <th>Current</th>
    <th>Room + Clicked</th>
  </tr>
  <tr>
    <td>Room</td>
    <td><Button variant="fab" color="primary" aria-label="Remove" onClick={this.subtract}>
                     <RemoveIcon />
              </Button></td>
    <td><Button variant="fab" color="secondary" aria-label="Add" onClick={this.add}>

<AddIcon />

</Button></td>
  </tr>
  <tr>
    <td>Adults</td>
    <td><Button variant="fab" color="primary" aria-label="Remove" onClick={this.subtract}>
                     <RemoveIcon />
              </Button></td>
    <td>Mexico</td>
  </tr>
  <tr>
    <td>Children</td>
    <td>Roland Mendel</td>
    <td>Austria</td>
  </tr>
  <tr>
    <td>Island Trading</td>
    <td>Helen Bennett</td>
    <td>UK</td>
  </tr>
  <tr>
    <td>Laughing Bacchus Winecellars</td>
    <td>Yoshi Tannamuri</td>
    <td>Canada</td>
  </tr>
  <tr>
    <td>Magazzini Alimentari Riuniti</td>
    <td>Giovanni Rovelli</td>
    <td>Italy</td>
  </tr>
</Table>
    </div>
      </div>
    );
  }
}

export default App;
